import "./App.css";
import { useState, useEffect } from "react";

const api = {
  base: "https://api.openweathermap.org/data/2.5/",
  key: "04e60fd3480011e71d9bebe70e77ab12",
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});
  const [forecast, setForecast] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `${api.base}weather?q=${search}&APPID=${api.key}`
        );
        const result = await response.json();
        if (result.cod === 200) {
          setWeather(result);
          console.log(result);
        } else {
          // Handle errors: display message, log details
          console.error("Error fetching weather:", result.message);
        }
      } catch (error) {
        console.error("Error fetching weather:", error);
      }
    };

    fetchData();
  }, [search]);

  useEffect(() => {
    const fetchForecast = async () => {
      try {
        const response = await fetch(
          `${api.base}forecast?q=${search}&APPID=${api.key}`
        );
        const result = await response.json();
        if (result.cod === "200") {
          setForecast(result.list);
          console.log(result.list);
        } else {
          // Handle errors: display message, log details
          console.error("Error fetching forecast:", result.message);
        }
      } catch (error) {
        console.error("Error fetching forecast:", error);
      }
    };

    fetchForecast();
  }, [search]);

  return (
    <div className="container">
      <div className="App">
        <h1>Weather Forecast</h1>

        <div>
          <input
            type="text"
            placeholder="Search city/state..."
            onChange={(e) => setSearch(e.target.value)}
          />
          <button onClick={() => searchPressed(search)}>Search</button>
        </div>

        {weather.hasOwnProperty("name") && (
          <>
            <p>City: {weather.name}</p>
            <p>Temperature: {Math.round(weather.main.temp - 273.15)}°C</p>
            <p>Description: {weather.weather[0].description}</p>
          </>
        )}

        {forecast.length > 0 && (
          <>
            <h2>5-Day Forecast:</h2>
            <div className="forecast">
              {forecast.slice(0, 5).map((item, index) => (
                <div key={index} className="forecast-item">
                  <p>Date: {item.dt_txt}</p>
                  <p>Temperature: {Math.round(item.main.temp - 273.15)}°C</p>
                  <p>Description: {item.weather[0].description}</p>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
