import "./App.css";
import { useState } from "react";

const api = {
  base: "https://api.openweathermap.org/data/2.5/",
  key: "04e60fd3480011e71d9bebe70e77ab12",
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});
  const [forecast, setForecast] = useState([]);

  const searchPressed = () => {
    fetch(`${api.base}weather?q=${search}&APPID=${api.key}`)
      .then((res) => res.json())
      .then((result) => {
        if (result.cod === 200) {
          setWeather(result);
          console.log(result);
        } else {
          // Handle errors: display message, log details
          console.error("Error fetching weather:", result.message);
        }
      });

    fetch(`${api.base}forecast?q=${search}&APPID=${api.key}`)
      .then((res) => res.json())
      .then((result) => {
        if (result.cod === "200") {
          setForecast(result);
          console.log(result);
        } else {
          console.error("error", result.message);
        }
      });
  };

  return (
    <div className="container">
      <div className="App">
        <h1>Weather Forecast</h1>

        <div>
          <input
            type="text"
            placeholder="Search city/state..."
            onChange={(e) => setSearch(e.target.value)}
          />
          <button onClick={searchPressed}>Search</button>
        </div>

        {weather.hasOwnProperty("name") && ( // Check if data is available
          <>
            <p>City: {weather.name}</p>
            <p>Temperature: {Math.round(weather.main.temp - 273.15)}Â°C </p>
            <p>Description: {weather.weather[0].description}</p>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
